mapper.select:
SELECT COUNT(uid)
  FROM tbl_member
 WHERE email = #{email}

SELECT uid, email, password, create_dt, created_by
  FROM tbl_member
 WHERE email = #{email}

SELECT password
  FROM tbl_member
 WHERE uid = #{uid}

mapper.update:
UPDATE tbl_member
   SET password = #{password}
 WHERE uid = #{uid}
